import React from 'react';
import { StyleSheet, Text, Image, View, FlatList, ScrollView } from 'react-native';
import {FontAwesome5} from '@expo/vector-icons';

export default function Feed(){

    const feed=[
        {
            id: 1,
            nome:'Piu Piu',
            imgPerfil: require('../assets/img/piupiu.jpg'),
            img:require('../assets/img/tinytoons.jpg'),
            descricao: 'Lorem ipsum dolor sit amet, consectetur adipisci elit, sed eiusmod tempor incidunt ut labore et dolore magna aliqua.',
            aspectRatio:1.77,
        },
        {
            id: 2,
            nome:'Perna',
            imgPerfil: require('../assets/img/pernalonga.png'),
            img:require('../assets/img/tinytoons3.jpg'),
            descricao: 'Lorem ipsum dolor sit amet, consectetur adipisci elit, sed eiusmod tempor incidunt ut labore et dolore magna aliqua.',
            aspectRatio:1.595,
        },
        {
            id: 3,
            nome:'Taz',
            imgPerfil: require('../assets/img/taz.jpg'),
            img:require('../assets/img/tinytoons2.jpg'),
            descricao: 'Lorem ipsum dolor sit amet, consectetur adipisci elit, sed eiusmod tempor incidunt ut labore et dolore magna aliqua.',
            aspectRatio:0.712,
        },
    ];

    function renderItem({item}){
        return <View >
            <View style={styles.postheader}>
                <View style={styles.postheaderesquerda}>
                    <Image style={styles.postheaderimg} source={item.imgPerfil}/>
                    <Text style={styles.texto}>{item.nome}</Text>
                </View>
                <FontAwesome5 style={styles.icones} name="ellipsis-h" size={20} />
            </View>
            <View style={{flexDirection: 'row'}}>
                <ScrollView horizontal={item.orientacao}>
                    <Image style={styles.postimg} aspectRatio={item.aspectRatio} source={item.img}/>
                    <Image style={styles.postimg} aspectRatio={item.aspectRatio} source={item.img}/>
                </ScrollView>
            </View>
            <View style={styles.footer}>
                <View style={styles.footericonleft}>
                    <FontAwesome5 style={styles.footericon} name="heart" size={25} />
                    <FontAwesome5 style={styles.footericon} name="comment" size={25} />
                    <FontAwesome5 style={styles.footericon} name="paper-plane" size={25} />
                </View>
                <View style={styles.footericoneright}>
                    <FontAwesome5 style={styles.icones} name="bookmark" size={25} />
                </View>
            </View>
            <Text style={styles.descricao}>{item.descricao}</Text>
        </View>
    }

    return(
        <View style={styles.feed}>
            <FlatList
                data={feed}
                renderItem={renderItem}
                keyExtractor={item=>item.id}
                showsVerticalScrollIndicator={false}
            />
        </View>
    );
}

const styles = StyleSheet.create({
    feed:{
      flex: 1,
    },
    postheader:{
      height: 60,
      backgroundColor: '#fff',
      flexDirection:'row',
      alignItems: 'center',
      justifyContent:'space-between',
      borderStartColor: 'gray',
      borderTopWidth: 0.3,
    },
    postheaderesquerda:{
      flexDirection:'row',
      margin:5,
    },
    postheaderimg:{
      height: 50,
      width:50,
      borderRadius: 25,
    },
    postimg:{
      width: '100%',
      height: undefined,
    },
    icones:{
      color:'black',
      alignSelf: 'center',
      margin: 10,
    },
    texto:{
      padding: 10,
      color:'black',
      fontSize: 20,
      fontWeight: 'bold',
    },
    footer:{
      height:60,
      backgroundColor: '#fff',
      flexDirection:'row',
      alignItems: 'center',
      justifyContent:'space-between',
    },
    footericon:{
      color:'black',
      margin: 10,
    },
    footericonleft:{
        flexDirection:'row',
    },
    footericoneright:{
        flexDirection:'row',
        margin:5,
    },
    descricao:{
        paddingBottom:10,
        marginLeft:10,
        textAlignVertical: 'center'
    },
  });
