import React from 'react';
import { StyleSheet, Text, Image, View, FlatList } from 'react-native';


export default function Stories(){
    const stories = [
        {
            id: 1,
            nome: 'Perna',
            src: require('../assets/img/pernalonga.png'),
        },
        {
            id: 2,
            nome: 'Frajola',
            src: require('../assets/img/frajola.jpg'),
        },
        {
            id: 3,
            nome: 'Patolino',
            src: require('../assets/img/patolino.jpg'),
        },
        {
            id: 4,
            nome: 'Tazz',
            src: require('../assets/img/taz.jpg'),
        },
        {
            id: 5,
            nome: 'Piu Piu',
            src: require('../assets/img/piupiu.jpg'),
        },
        {
            id: 6,
            nome: 'Felicia',
            src: require('../assets/img/felicia.png'),
        },
    ];

    function renderItem({item}){
        return <View style={styles.story}>
                <Image style={styles.imgstory} source={item.src}/>
                <Text>{item.nome}</Text>
            </View>
    }

    return(
        <View style={styles.storys}>
            <FlatList 
                data={stories}
                renderItem={renderItem}
                keyExtractor={item => item.id}
                horizontal
                showsHorizontalScrollIndicator={false}
            />
        </View>
    );
}

const styles = StyleSheet.create({
    storys:{    
      height: 90,
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    story:{
      height: 90,
      width: 90,
      alignItems: 'center',
    },
    imgstory:{
      width: 70,
      height: 70,
      borderRadius: 35,
      borderColor: '#32cd32',
      borderWidth: 3,
    },
});
